<script>
const CURSOR_TIMEOUT = 500.0 // hide the cursor after X miliseconds
const HISTORY_LENGTH = 128 // average over the last N notes
const DAMPING_CONSTANT = 1000.0 // in miliseconds

var TwoLib = require('two.js')

const PLAYER_NAME = 'Volume'

var playerStatus = 'uninitialized'

function initialize () {
  playerStatus = 'initializing'

  playerStatus = 'initialized'
}

function makeNotes (mystate) {
  var two = mystate.two

  var shapes = mystate.shapes
  shapes.forEach(s => {
    two.remove(s)
  })
  shapes = []

  var i
  var stops = []
  for (i = 0; i < 128; i++) {
    // x-coord
    stops.push(i)

    // y-coord
    stops.push(0)
  }
  var path = two.makePath(...stops, true)
  path.curved = false
  path.fill = '0xeeeeee'
  mystate.shapes.push(path)

  var cursor = two.makePolygon(64, 5, 1, 3)
  cursor.stroke = 'red'
  mystate.cursor = cursor
}

module.exports = {
  data () {
    initialize()
    return {
      width: 840,
      height: 600
    }
  },
  methods: {
    noteOn (number, velocity, timestamp) {
      var v = Math.floor(128 * velocity)

      var historyPointer = this.mystate.historyPointer
      this.mystate.history[historyPointer] = v
      this.mystate.historyPointer = (historyPointer + 1) % HISTORY_LENGTH

      this.mystate.inactiveTime = 0

      this.mystate.volumes[v] += 10.0
    },
    tick (piano, elapsed) {
      var p = this.$refs['twojs']
      if (playerStatus !== 'initialized' || !p) {
        return
      }

      // render
      var two = this.mystate.two

      // update page size
      if (p.offsetWidth !== this.width || p.offsetHeight !== this.height) {
        // resize the two canvas
        two.renderer.setSize(p.offsetWidth, p.offsetHeight)

        // keep the canvas property in sync
        two.width = p.offsetWidth
        two.height = p.offsetHeight

        // keep the vue component in sync
        this.width = two.width
        this.height = two.height

        two.scene.translation.set(0, this.height / 2)
        two.scene.scale = two.width / 128.0
      }

      // v = v0 exp (-t/t0)
      var damping = Math.exp(-elapsed / DAMPING_CONSTANT)

      var path = this.mystate.shapes[0]
      for (let i = 1; i < 128; i++) {
        var damped = Math.min(this.mystate.volumes[i] * damping, 100)
        this.mystate.volumes[i] = damped > 0.1 ? damped : 0
        path.vertices[i].y = -this.mystate.volumes[i]
      }

      // calculate new cursor position by averaging over the history
      this.mystate.inactiveTime += elapsed

      if (this.mystate.inactiveTime > CURSOR_TIMEOUT) {
        // no recent activity, clear the history
        this.mystate.history.fill(0)
      }

      var sum = 0
      var count = 0
      this.mystate.history.forEach(v => {
        if (v > 0) {
          sum += v
          count += 1
        }
      })

      if (count > 0) {
        // move the cursor
        var oldX = this.mystate.cursor.position.x
        var targetX = sum / count
        if (oldX > 0) {
          var direction = targetX > oldX ? 1 : -1
          var speed = 0.001 * (targetX - oldX) ** 2
          this.mystate.cursor.position.x = oldX + direction * speed * elapsed
        } else {
          this.mystate.cursor.position.x = targetX
        }
      } else {
        // hide the cursor
        this.mystate.cursor.position.x = -10
      }

      two.update()
    }
  },
  mounted () {
    var p = this.$refs['twojs']
    this.width = p.offsetWidth
    this.height = p.offsetHeight

    var two = new TwoLib.Two({
      type: TwoLib.Two.Types.canvas,
      width: this.width,
      height: this.height,
      fullscreen: false
    })
    two.scene.translation.set(0, this.height / 2)
    two.scene.scale = this.width / 128.0
    two.appendTo(this.$refs['twojs'])

    this.mystate = {
      cursor: null,
      shapes: [],
      historyPointer: 0,
      history: Array(HISTORY_LENGTH).fill(0),
      inactiveTime: 0,
      volumes: Array(128).fill(0),
      two: two
    }

    makeNotes(this.mystate)

    this.$store.commit('addPlayer', {
      name: PLAYER_NAME,
      tick: this.tick,
      noteOn: this.noteOn,
      scope: this
    })
  },
  beforeDestroy () {
    this.$store.commit('removePlayer', PLAYER_NAME)
  }
}
